(function(){tinymce.PluginManager.requireLangPack('pastecode');tinymce.create('tinymce.plugins.PasteCodePlugin',{init:function(ed,url){ed.addCommand('mcePasteCode',function(){ed.windowManager.open({file:url+'/pastecode.htm',width:520,height:320,inline:1},{plugin_url:url})});ed.addButton('pastecode',{title:'pastecode.desc',cmd:'mcePasteCode',image:url+'/img/tag.png'})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Paste code plugin',author:'LemonStand eCommerce Inc.',authorurl:'http://lemonstand.com',infourl:'http://lemonstand.com',version:"1.0"}}});tinymce.PluginManager.add('pastecode',tinymce.plugins.PasteCodePlugin)})();